package day04;

public class TypeCasting001 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        int by = 21;
		
		long sayı2 = by;
		
		System.out.println(sayı2);
		
        int bay = 201;
		
		short sayı222 = (short) bay; // java bğtğk data tipini kücük data tipine bu şekilde yapıyoruz,
		System.out.println(sayı222);
		
		
		double sayı3 =23.9;
		int   sayı4  =  (int) sayı3 ;
		
		System.out.println(sayı4);
		
		float sayı5 = 32.5f;
		short syı6 = (short) sayı5;
		System.out.println(syı6);
		
		double a = 2.2;
	    double b = 1.2;
	    double sonuc = a/b;
	    System.out.println(sonuc);
	    
	    int sonuc2 = (int) (a/b);
	    System.out.println(sonuc2);
		
		
		
		
		
		
	}

}
